beer1 = Beer.create!(name: "All Green Everything", brewery_id: other_half.id, style: "IPA", ibu: "120", description: "A huge, but dry, triple IPA packed with Motueka, Amarillo, Citra and Mosaic.", abv: "10.5")
beer2 = Beer.create!(name: "Double Dry Hopped Double Mosaic Dream", brewery_id: other_half.id, style: "IPA", ibu: "90", description: "Double Dry Hopped Double Mosaic Dream is our standard Double Mosaic dry hopped a second time with a mosaic lupulin powder. Lupulin powder is a new hop product that is similar to our standard hops but with all the extra plant filler stripped away. It’s pure hops and it’s awesome.", abv: "8.5")
beer3 = Beer.create!(name: "Green Diamonds", brewery_id: other_half.id, style: "IPA", ibu: "100", description: "An imperial IPA brewed with Golden Promise malt and lots of Australian Galaxy hops.", abv: "9.1")

beer4 = Beer.create!(name: "Double Negative", brewery_id: grimm.id, style: "Sour", ibu: "75", description: "2015 GABF gold medal and 2014 GABF silver medal in the imperial stout category. Double Negative pours jet black with burnt orange highlights. A long lasting tan head leaves abundant lacing. Aromas bursting from the glass include espresso, bitter chocolate, caramel, black currant and blueberry. Creamy and viscous on the palate, the flavor initially follows the nose. A pop of smooth bitterness comes after, and then a lingering, roasty finish.", abv: "10")
beer5 = Beer.create!(name: "Lambo Door", brewery_id: grimm.id, style: "IPA", ibu: "80", description: "We in this bitch. Finna get crunk. Gummi bear juice on fleek. Citra, Simcoe, El Dorado you already know what it is.", abv: "8")
beer6 = Beer.create!(name: "Rainbow Dome", brewery_id: grimm.id, style: "Sour", ibu: "10", description: "American wild/sour ale brewed with apricots, dry hopped with El Dorado, Columbus, and Cascade, and conditioned on oak. Crush crush crush.", abv: "5")

beer7 = Beer.create!(name: "Pliny The Elder", brewery_id: russian_river.id, style: "IPA", ibu: "100", description: "A true leader in the hop-wars of the west coast, Pliny the Elder hits you over the head with hoppy bitterness and manages to smooth the rough edges out enough to become an enjoyable brew.", abv: "8")
beer8 = Beer.create!(name: "Pliny The Younger", brewery_id: russian_river.id, style: "IPA", ibu: "90", description: "Pliny the Younger was Pliny the Elder’s nephew, in the case of this beer, the 'Younger' is a triple IPA. Pliny the Younger is hopped three times more than our standard IPA, and is dry hopped four different times.", abv: "10.25")
beer9 = Beer.create!(name: "Consecration", brewery_id: russian_river.id, style: "Sour", ibu: "10", description: "Dark Ale aged in Cabernet Sauvignon barrels from local wineries. It is aged for 4 to 8 months with black currants, brettanomyces, lactobacillus, and pediococcus added to each barrel. Rich flavors of chocolate truffle, spice, tobacco, currants, and a bit of Cabernet. Very full-bodied, sip slowly!", abv: "10")

# beer10 = Beer.create!(name: "Heady Topper", brewery_id: alchemist.id, style: "IPA", ibu: "100", description: "We love hops – that’s why our flagship Double IPA, Heady Topper, is packed full of them. Heady Topper was designed to showcase the complex flavors and aromas these flowers produce. The Alchemist has been brewing Heady Topper since 2003. This Double IPA is not intended to be the strongest or most bitter DIPA. It is brewed to give you wave after wave of hop flavor without any astringent bitterness. We brew Heady Topper with a proprietary blend of six hops – each imparting its own unique flavor and aroma. Take a big sip of Heady and see what hop flavors you can pick out. Orange? Tropical Fruit? Pink Grapefruit? Pine? Spice? There is just enough malt to give this beer some backbone, but not enough to take the hops away from the center stage.", abv: "8")
# beer11 = Beer.create!(name: "Focal Banger", brewery_id: alchemist.id, style: "IPA", ibu: "90", description: "American IPA with Citra & Mosaic hops.", abv: "7")
# beer12 = Beer.create!(name: "Luscious", brewery_id: alchemist.id, style: "Stout", ibu: "No", description: "British-style imperial stout", abv: "9.2")

# beer13 = Beer.create!(name: "White", brewery_id: allagash.id, style: "Wheat", ibu: "13", description: "Allagash White features a refreshing balance of citrus and spice. Wheat, coriander, and Curaçao orange peel round out the flavor of this pale straw-colored, hazy beer.", abv: "5.2")
# beer14 = Beer.create!(name: "Coolship Resurgam", brewery_id: allagash.id, style: "Sour", ibu: "20", description: "Coolship Resurgam is a blend of both old and young unfruited spontaneous beer. The name comes from the motto of our fair city, Portland, Me. It means 'I shall rise again'. Coolship Resurgam won a Silver medal at the 2010 GABF.", abv: "6.6")
# beer15 = Beer.create!(name: "Curieux", brewery_id: allagash.id, style: "Belgian", ibu: "27", description: "First brewed back in 2004, Curieux was our initial foray into barrel aging. To make Curieux, we take our Tripel and let it age in bourbon barrels for six-to-eight weeks. Once that time is up, we take the beer out of our cold cellars and blend it back with a portion of fresh Tripel. The resulting rich, golden ale features smooth notes of coconut, vanilla, and a hint of bourbon. ", abv: "10.4")

# beer16 = Beer.create!(name: "Pale Ale", brewery_id: sierra_nevada.id, style: "Pale Ale", ibu: "38", description: "Our most popular beer, Sierra Nevada Pale Ale is a delightful interpretation of a classic style. It has a deep amber color and an exceptionally full-bodied, complex character. Generous quantities of premium Cascade hops give the Pale Ale its fragrant bouquet and spicy flavor.", abv: "5.6")
# beer17 = Beer.create!(name: "Torpedo Extra IPA", brewery_id: sierra_nevada.id, style: "IPA", ibu: "65", description: "Sierra Nevada Torpedo Ale is a big American IPA; bold, assertive and full of flavor and aromas highlighting the complex citrus, pine and herbal character of whole-cone American hops. Our obsession with harnessing huge hop flavor led to the development of what we call the hop torpedo, a revolutionary method of dry-hopping designed, built, and debuted here at the brewery. Our torpedo is a sleek, stainless-steel piece of hardware that delivers more pure hop aroma than any method of dry-hopping we’ve ever seen.", abv: "7.2")
# beer18 = Beer.create!(name: "Kellerweis", brewery_id: sierra_nevada.id, style: "Wheat", ibu: "15", description: "One of the only American Hefeweizens made using the traditional Bavarian style of open fermentation. This difficult and labor-intensive technique adds uncommon depth and flavor complexity. A deeply flavorful hazy-golden hefeweizen, it is refreshing and perfect for a sunny day.", abv: "4.8")

# beer19 = Beer.create!(name: "Stone Xocoveza", brewery_id: stone.id, style: "Stout", ibu: "50", description: "This insanely delicious take on Mexican hot chocolate is crafted with cocoa, coffee, pasilla peppers, vanilla, cinnamon and nutmeg. First brewed in 2014 with San Diego homebrewer Chris Banker after his recipe was named the winner of our annual homebrew competition, it quickly became a cross-country sensation as craft beer media and fans cheered for Stone Xocoveza…and clamored for more. We too loved this beer, so of course we caroled ¡Claro que sí!", abv: "8.1")
# beer20 = Beer.create!(name: "Stone IPA", brewery_id: stone.id, style: "IPA", ibu: "71", description: "By definition, an India Pale Ale is hoppier and higher in alcohol than its little brother, pale ale — and we deliver in spades. Now one of the most well respected and best-selling IPAs in the country, this golden beauty explodes with citrusy flavor and hop aromas, all perfectly balanced by a subtle malt character.", abv: "6.9")
# beer21 = Beer.create!(name: "Stone Smoked Porter", brewery_id: stone.id, style: "Porter", ibu: "53", description: "Stone Smoked Porter is dark, rich and complicated. A porter is substantially darker than a pale ale, but not as black or opaque as a stout. Our Smoked porter has a captivatingly deep mahogany color, complimented by silky dark tan head. Rich, full bodied and robust. Smooth, with chocolate and coffee-like flavors balanced by the subtle smoky character of just the right amount of peat-smoked specialty malt.", abv: "5.9")

# beer22 = Beer.create!(name: "Sip of Sunshine", brewery_id: lawsons.id, style: "IPA", ibu: "65", description: "This lupulin-ladin India Pale Ale is packed with juicy tropical fruit character, bright floral aromas and delectable layers of hop flavor. Pour mindfully, inhale deeply and enjoy a tropical vacation in a glass. Always store cold, enjoy fresh and stay cool!", abv: "8")
# beer23 = Beer.create!(name: "Maple Nipple", brewery_id: lawsons.id, style: "Amber Ale", ibu: "25", description: "Our robust, rich maple ale is loaded with 100% pure Vermont maple syrup. It delivers a smooth and palate pleasing mouth feel with a slightly sticky finish!", abv: "8")
# beer24 = Beer.create!(name: "Super Session #8", brewery_id: lawsons.id, style: "IPA", ibu: "35", description: "Mosaic Hops", abv: "4.8")

# beer25 = Beer.create!(name: "Field 5 Golden IPA", brewery_id: gsb.id, style: "IPA", ibu: "70", description: "Follow these simple instructions on how to get the most enjoyment out of your can of Field 5 Golden IPA. By reading this can, you've already started your journey. Once you get back in your car, head east on Drexel Drive. Make a right on Fifth Avenue & head south to the third light. Make a right onto Spur Drive North & make a quick left onto the ramp for Southern State Parkway West. Take Exit 40 for Robert Moses Causeway. Continue south over both sets of bridges until you reach the traffic cirlce, take the second exit & continue onto Burma Road. When you see the sign for parking Field 5, make a right into the parking lot & head on out to the beach, find a nice relaxing spot, pop open this can & enjoy.", abv: "7")
# beer26 = Beer.create!(name: "Blood Orange Pale Ale", brewery_id: gsb.id, style: "Pale Ale", ibu: "40", description: "April 5, 2012: Remember back to the first time you ventured across the Great South Bay via the Robert Moses Causeway on your way to the beach? Well, we’ve captured that feeling in our Robert Moses Pale Ale! This well-balanced, sessionable American-style pale ale will keep you refreshed all year round. Whether you’re fishing on your boat, sitting around a campfire, or relaxing in your back yard, the fresh aroma of American hops paired with the crisp taste of the pale malts will bring you back every time. We’ve taken the Robert Moses Pale Ale that you’ve come to enjoy and took it to the next level with the addition of blood oranges to give it a slightly more citrus character with just the right amount of bitterness you’d expect from an American-style pale ale.", abv: "5")
# beer27 = Beer.create!(name: "Snaggletooth Stout", brewery_id: gsb.id, style: "Stout", ibu: "61", description: "Like its namesake, this beer is something you could get tangled up in.
# Brewed in limited quantities , especially popular during the colder months, this beer combines unique ingredients including roasted chocolate malt, flaked oats, cinnamon, licorice root and Long Island apples , culminating in a savory full-bodied malty goodness, and finishing with a hardy bite.", abv: "6.5")

# beer28 = Beer.create!(name: "Mary", brewery_id: hill_farmstead.id, style: "Pilsner", ibu: "60", description: "Mary was Edward's mother and our great-grandmother. A German-style pilsner brewed with our well water, German pilsner malt, our favorite German hops, and classic German lager yeast. 4.6% abv.", abv: "4.6")
# beer29 = Beer.create!(name: "Edward", brewery_id: hill_farmstead.id, style: "Pale Ale", ibu: "65", description: "Edward (1917-2002) is our grandfather; Hill Farmstead Brewery rests upon the land that was once home to him and his five children. In his honor, this American Pale Ale is dutifully crafted from American malted barley, a plethora of American hops, our ale yeast and water from Edward’s well. It is unfiltered and dry hopped. Aromatic and flowery, with impressions of citrus and pine, this is the ale that I dream to have shared with Edward. Pale and Caramel malt; Centennial, Chinook, Columbus, Simcoe, and Warrior hops; House Ale Yeast, and our Well Water. ", abv: "5.2")
# beer30 = Beer.create!(name: "Everett", brewery_id: hill_farmstead.id, style: "Porter", ibu: "30", description: "Everett (1908–1939) was our grandfather’s brother; Hill Farmstead Brewery rests upon the land that was once home to him and his 13 siblings. In his honor, this Porter is crafted from American malted barley, English and German roasted malts, American hops, our ale yeast, and water from our well. It is unfiltered and naturally carbonated. Decadent in its depth, with a complex backbone of chocolate, coffee, and malty sweetness, this is the ale that I dream to have shared with Everett.", abv: "7.5")

# beer31 = Beer.create!(name: "Rosé de Gambrinus", brewery_id: cantillon.id, style: "Sour", ibu: "10", description: "Beer with a slightly acidic, fruity and fragrant taste. Just like kriek, the 'fruitiness' of Rosé de Gambrinus is at its best when the beer is young. With age the lambic will take the upper hand, but this is at the expense of the fruit component.", abv: "5")
